import React from 'react';
import './ChessPiece.css';

const pieceImages = {
  'K': '/chess_pieces/wk.png',
  'Q': '/chess_pieces/wq.png',
  'R': '/chess_pieces/wr.png',
  'B': '/chess_pieces/wb.png',
  'N': '/chess_pieces/wn.png',
  'P': '/chess_pieces/wp.png',
  'k': '/chess_pieces/bk.png',
  'q': '/chess_pieces/bq.png',
  'r': '/chess_pieces/br.png',
  'b': '/chess_pieces/bb.png',
  'n': '/chess_pieces/bn.png',
  'p': '/chess_pieces/bp.png'
};

const ChessPiece = ({ piece }) => {
  if (!piece) return null;
  
  return (
    <img 
      src={pieceImages[piece]} 
      alt={piece} 
      className={`chess-piece ${piece === piece.toUpperCase() ? 'white' : 'black'}`}
    />
  );
};

export default ChessPiece;
