import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import io from 'socket.io-client';
import ChessPiece from './ChessPiece';
import './Game.css';

const Game = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [game, setGame] = useState(null);
  const [selectedPiece, setSelectedPiece] = useState(null);
  const [possibleMoves, setPossibleMoves] = useState([]);
  const [error, setError] = useState('');
  const [socket, setSocket] = useState(null);
  const [lastMove, setLastMove] = useState(null);
  const [playerColor, setPlayerColor] = useState(null);
  const [isConnected, setIsConnected] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    const user = JSON.parse(localStorage.getItem('user'));
    
    if (!token || !user) {
      navigate('/login');
      return;
    }

    // Connect to socket with authentication
    const newSocket = io('http://localhost:5000', {
      auth: { token },
      transports: ['websocket'],
      reconnection: true,
      reconnectionAttempts: 5
    });

    newSocket.on('connect', () => {
      console.log('Socket connected');
      setIsConnected(true);
    });

    newSocket.on('connect_error', (error) => {
      console.error('Socket connection error:', error);
      setError('Failed to connect to game server');
    });

    newSocket.on('disconnect', () => {
      console.log('Socket disconnected');
      setIsConnected(false);
    });

    setSocket(newSocket);

    // Join game room
    newSocket.emit('join-game', { 
      gameId: id,
      playerId: user._id
    });

    // Listen for game updates
    newSocket.on('game-updated', ({ game: updatedGame, lastMoveInfo }) => {
      console.log('Game updated:', updatedGame);
      setGame(updatedGame);
      if (lastMoveInfo) {
        setLastMove({
          from: lastMoveInfo.from,
          to: lastMoveInfo.to,
          captured: lastMoveInfo.captured
        });
      }
    });

    // Listen for errors
    newSocket.on('error', ({ message }) => {
      console.error('Game error:', message);
      setError(message);
    });

    // Fetch initial game state
    fetchGame();

    return () => {
      if (newSocket) {
        newSocket.disconnect();
      }
    };
  }, [id, navigate]);

  const fetchGame = async () => {
    try {
      const token = localStorage.getItem('token');
      const user = JSON.parse(localStorage.getItem('user'));
      const response = await axios.get(`http://localhost:5000/api/games/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      const gameData = response.data.game;
      setGame(gameData);
      
      // Set player color
      if (gameData.players.white === user._id) {
        setPlayerColor('white');
      } else if (gameData.players.black === user._id) {
        setPlayerColor('black');
      }
    } catch (err) {
      console.error('Error fetching game:', err);
      setError('Failed to fetch game');
    }
  };

  const handleSquareClick = async (row, col) => {
    if (!game || game.status !== 'active') return;

    const piece = game.board[row][col];
    const isWhitePiece = piece && piece === piece.toUpperCase();
    const isWhiteTurn = !game.turn;

    if (selectedPiece) {
      // Try to move the piece
      const [fromRow, fromCol] = selectedPiece;
      const user = JSON.parse(localStorage.getItem('user'));

      // Check if the move is in possible moves
      const isValidMove = possibleMoves.some(
        move => move[0] === row && move[1] === col
      );

      if (!isValidMove) {
        setSelectedPiece(null);
        setPossibleMoves([]);
        return;
      }

      // Make the move
      socket.emit('make-move', {
        gameId: id,
        from: [fromRow, fromCol],
        to: [row, col],
        playerId: user._id
      });

      setSelectedPiece(null);
      setPossibleMoves([]);
    } else if (piece) {
      // Select a piece
      if (isWhitePiece === isWhiteTurn && 
          ((isWhitePiece && playerColor === 'white') || 
           (!isWhitePiece && playerColor === 'black'))) {
        
        // Get possible moves for this piece
        try {
          const token = localStorage.getItem('token');
          const response = await axios.post(
            `http://localhost:5000/api/games/${id}/possible-moves`,
            { from: [row, col] },
            { headers: { Authorization: `Bearer ${token}` } }
          );
          
          if (response.data.moves && response.data.moves.length > 0) {
            setSelectedPiece([row, col]);
            setPossibleMoves(response.data.moves);
          }
        } catch (err) {
          console.error('Error getting possible moves:', err);
          setError('Failed to get possible moves');
        }
      }
    }
  };

  const isSquareHighlighted = (row, col) => {
    if (selectedPiece && possibleMoves.some(move => move[0] === row && move[1] === col)) {
      return 'possible-move';
    }
    if (lastMove) {
      if ((lastMove.from[0] === row && lastMove.from[1] === col) ||
          (lastMove.to[0] === row && lastMove.to[1] === col)) {
        return lastMove.captured ? 'capture-move' : 'last-move';
      }
    }
    return '';
  };

  if (!game) {
    return <div className="game-container">Loading game...</div>;
  }

  return (
    <div className="game-container">
      <div className="game-header">
        <h1>Chess Game</h1>
        <button onClick={() => navigate('/lobby')}>Back to Lobby</button>
      </div>

      {error && <div className="error-message">{error}</div>}
      {!isConnected && <div className="error-message">Disconnected from game server. Attempting to reconnect...</div>}

      <div className="chess-board">
        {game.board.map((row, rowIndex) => (
          <div key={rowIndex} className="board-row">
            {row.map((piece, colIndex) => (
              <div
                key={`${rowIndex}-${colIndex}`}
                className={`board-square ${(rowIndex + colIndex) % 2 === 0 ? 'white' : 'black'} ${
                  selectedPiece && selectedPiece[0] === rowIndex && selectedPiece[1] === colIndex
                    ? 'selected'
                    : ''
                } ${isSquareHighlighted(rowIndex, colIndex)}`}
                onClick={() => handleSquareClick(rowIndex, colIndex)}
              >
                <ChessPiece piece={piece} />
              </div>
            ))}
          </div>
        ))}
      </div>

      <div className="game-info">
        <div className="status">
          Status: {game.status}
          {game.inCheck && ` - ${game.checkPlayer} is in check!`}
        </div>
        <div className="turn">
          Current Turn: {game.turn ? 'Black' : 'White'}
          {playerColor && ` (You are ${playerColor})`}
        </div>
      </div>
    </div>
  );
};

export default Game;
