import React, { useState } from 'react';
import './App.css';
import './chess_pieces';

const initialBoard = [
  ['R', 'N', 'B', 'Q', 'K', 'B', 'N', 'R'],
  ['P', 'P', 'P', 'P', 'P', 'P', 'P', 'P'],
  ['', '', '', '', '', '', '', ''],
  ['', '', '', '', '', '', '', ''],
  ['', '', '', '', '', '', '', ''],
  ['', '', '', '', '', '', '', ''],
  ['p', 'p', 'p', 'p', 'p', 'p', 'p', 'p'],
  ['r', 'n', 'b', 'q', 'k', 'b', 'n', 'r']
];

const pieceImages = {
  'R': '/chess_pieces/white_rook.svg',
  'N': '/chess_pieces/white_knight.svg',
  'B': '/chess_pieces/white_bishop.svg',
  'Q': '/chess_pieces/white_queen.svg',
  'K': '/chess_pieces/white_king.svg',
  'P': '/chess_pieces/white_pawn.svg',
  'r': '/chess_pieces/black_rook.svg',
  'n': '/chess_pieces/black_knight.svg',
  'b': '/chess_pieces/black_bishop.svg',
  'q': '/chess_pieces/black_queen.svg',
  'k': '/chess_pieces/black_king.svg',
  'p': '/chess_pieces/black_pawn.svg'
};

const Chessboard = () => {
  const [board, setBoard] = useState(initialBoard);
  const [selected, setSelected] = useState(null);

  const handleClick = (rowIndex, colIndex) => {
    if (selected && !board[selected[0]][selected[1]]) {
      setSelected(null);
      return;
    }
    if (selected) {
      movePiece(selected, [rowIndex, colIndex]);
    } else {
      setSelected([rowIndex, colIndex]);
    }
  };

  const movePiece = (from, to) => {
    const [fromRow, fromCol] = from;
    const [toRow, toCol] = to;
    const newBoard = board.map((row, index) => [...row]);

    newBoard[toRow][toCol] = newBoard[fromRow][fromCol];
    newBoard[fromRow][fromCol] = '';

    setBoard(newBoard);
    setSelected(null);
  };

  return (
    <div className="chessboard">
      {board.map((row, rowIndex) => (
        <div key={rowIndex} className="row">
          {row.map((piece, colIndex) => (
            <div
              key={colIndex}
              className={`square ${((rowIndex + colIndex) % 2 === 0) ? 'white' : 'black'}`}
              onClick={() => handleClick(rowIndex, colIndex)}
            >
              {piece && (
                <img
                  src={pieceImages[piece]} 
                  alt={piece} 
                  className="piece"
                />
              )}
            </div>
          ))}
        </div>
      ))}
    </div>
  );
};

function App() {
  return (
    <div className="App">
      <h1>Chess Game</h1>
      <Chessboard />
    </div>
  );
}

export default App;
